mapper.select:
SELECT * FROM knowledge_entry
WHERE deleted = 0

    AND (
        MATCH(title, content) AGAINST(#{keyword} IN NATURAL LANGUAGE MODE)
        OR title LIKE CONCAT('%', #{keyword}, '%')
        OR content LIKE CONCAT('%', #{keyword}, '%')
    )


    AND source_id IN

        #{id}



    AND (

        JSON_CONTAINS(tags, JSON_QUOTE(#{tag}))

    )


    AND published_at >= #{startDate}


    AND published_at <= #{endDate}

ORDER BY published_at DESC

SELECT * FROM knowledge_entry WHERE deleted = 0
AND vector_id IN
#{id}

mapper.update:
UPDATE knowledge_entry
SET view_count = view_count + 1
WHERE id = #{id}
