parameters.jsCode:
const rssSources = [
  {
    id: 1,
    name: '少数派',
    url: 'https://sspai.com/feed'
  }
];

return rssSources.map(source => ({ json: source }));

const httpResponse = $json;
const rssData = $('读取RSS').item.json;

let html = httpResponse.data || httpResponse.body || httpResponse.response || '';
if (typeof html === 'object') {
  html = html.body || html.data || JSON.stringify(html);
}
html = String(html || '');

let content = '';
if (html && html.length > 100) {
  let cleanHtml = html
    .replace(/<script[^>]*>.*?<\/script>/gis, '')
    .replace(/<style[^>]*>.*?<\/style>/gis, '')
    .replace(/<!--.*?-->/gs, '');

  const articleMatch = cleanHtml.match(/<article[^>]*>(.*?)<\/article>/is);
  if (articleMatch) {
    cleanHtml = articleMatch[1];
  }

  content = cleanHtml
    .replace(/<[^>]+>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&[a-z]+;/gi, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  if (content.length > 5000) {
    content = content.substring(0, 5000) + '...';
  }
}

if (!content || content.length < 50) {
  content = rssData.description || rssData.contentSnippet || '无内容';
}

return {
  title: rssData.title || '无标题',
  content: content,
  sourceUrl: rssData.link || '',
  publishedAt: rssData.isoDate || rssData.pubDate || new Date().toISOString(),
  author: rssData.creator || rssData.author || 'Unknown',
  tags: rssData.categories || []
};

