parameters.jsCode:
const aiResponse = $json;
const originalData = $('提取内容').item.json;

let summary = '';
if (aiResponse.response && typeof aiResponse.response === 'string') {
  summary = aiResponse.response.trim();
}

return {
  title: originalData.title,
  content: originalData.content,
  summary: summary,
  sourceUrl: originalData.sourceUrl,
  publishedAt: originalData.publishedAt,
  author: originalData.author,
  tags: originalData.tags || [],
  contentType: 'news',
  dataSource: 'RSS'
};

const items = $input.all();
const successCount = items.filter(item => 
  (item.json.statusCode === 200 || item.json.code === 200)
).length;

return {
  timestamp: new Date().toISOString(),
  total: items.length,
  successCount: successCount,
  failedCount: items.length - successCount,
  message: `RSS采集完成: 成功 ${successCount}/${items.length} 条`
};
